import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { FaMoon, FaSun, FaGlobe, FaUser } from "react-icons/fa";
import "./Navbar.css";

const Navbar = () => {
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);
  const navigate = useNavigate();

  // Check login status on mount
  useEffect(() => {
    const token = localStorage.getItem("token");
    setIsLoggedIn(!!token);
  }, []);

  const handleThemeToggle = () => {
    setIsDarkMode(!isDarkMode);
    document.body.classList.toggle("dark-mode", !isDarkMode);
  };

  const handleLanguageChange = () => {
    alert("Language change not implemented yet!");
  };

  const handleProfileClick = () => {
    // Toggle the profile dropdown on click
    setShowProfileDropdown(!showProfileDropdown);
  };

  const handleLogout = () => {
    // Remove token and update login state
    localStorage.removeItem("token");
    setIsLoggedIn(false);
    setShowProfileDropdown(false);
    navigate("/");
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <Link to="/" className="logo-link">
          <img
            src="/images/logo.png"
            alt="Militex Logo"
            className="logo-image"
          />
        </Link>
      </div>

      <ul className="navbar-links">
        <li>
          <Link to="/">Home</Link>
        </li>
        <li>
          <Link to="/buy">Buy</Link>
        </li>
        <li>
          <Link to="/sell">Sell</Link>
        </li>
        <li>
          <Link to="/">Foundation</Link>
        </li>
      </ul>

      <div className="navbar-right">
        <button onClick={handleThemeToggle} className="icon-button">
          {isDarkMode ? <FaSun /> : <FaMoon />}
        </button>
        <button onClick={handleLanguageChange} className="icon-button">
          <FaGlobe />
        </button>
        <div className="profile-wrapper">
          <button onClick={handleProfileClick} className="icon-button">
            <FaUser />
          </button>
          {showProfileDropdown && (
            <div className="profile-dropdown">
              {!isLoggedIn ? (
                <>
                  <Link
                    to="account/login"
                    onClick={() => setShowProfileDropdown(false)}
                  >
                    Log In
                  </Link>
                  <Link
                    to="account/signup"
                    onClick={() => setShowProfileDropdown(false)}
                  >
                    Sign Up
                  </Link>
                </>
              ) : (
                <>
                  <Link
                    to="/profile"
                    onClick={() => setShowProfileDropdown(false)}
                  >
                    Profile
                  </Link>
                  <button onClick={handleLogout}>Log Out</button>
                </>
              )}
            </div>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
