import React, { useState, useEffect } from 'react';
import api from '../../services/api';

const ApiTest = () => {
  const [status, setStatus] = useState('Loading...');
  const [error, setError] = useState(null);

  useEffect(() => {
    const testConnection = async () => {
      try {
        // Try to fetch a simple endpoint - replace with an actual endpoint from your API
        const response = await api.get('users/');
        setStatus(`Connected! Server returned ${response.status} status code`);
      } catch (err) {
        console.error('API connection error:', err);
        setError(err.message || 'Failed to connect to the API');
        setStatus('Failed');
      }
    };

    testConnection();
  }, []);

  return (
    <div className="p-6 bg-white rounded-lg shadow-md">
      <h2 className="text-xl font-bold text-indigo-900 mb-4">API Connection Test</h2>

      <div className={`p-4 rounded-lg ${
        error ? 'bg-red-100 text-red-700' : 'bg-green-100 text-green-700'
      }`}>
        <p><strong>Status:</strong> {status}</p>
        {error && (
          <div className="mt-2">
            <p><strong>Error:</strong> {error}</p>
            <div className="mt-2 text-sm">
              <p><strong>Troubleshooting:</strong></p>
              <ul className="list-disc pl-5">
                <li>Make sure your Django server is running on port 8000</li>
                <li>Check for CORS issues in browser console</li>
                <li>Verify API URL configuration in api.js</li>
                <li>Check network tab for detailed error responses</li>
              </ul>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ApiTest;
