import React, { useState } from "react";
import {Link, useNavigate} from "react-router-dom";
import FormInput from "../components/FormInput";
import FormButton from "../components/FormButton";
import "./LoginPage.css";

function LoginPage() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();

    // Example API call (adjust URL and method as needed)
    fetch("http://127.0.0.1:8000/api/login/", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ username, password }),
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.token) {
          // Save the token (if using token-based auth)
          localStorage.setItem("token", data.token);
          // Update the state to indicate the user is logged in
          setIsLoggedIn(true);
          // Optionally navigate to the homepage or profile
          navigate("/");
        } else {
          // Handle errors (e.g., show error messages)
          alert("Login failed: " + JSON.stringify(data));
        }
      })
      .catch((error) => console.error("Login error:", error));
  };


  return (
    <div className="login-page">
      <div className="login-container">
        <h2>Log In</h2>
        <form onSubmit={handleSubmit}>
          <FormInput
            label="Username"
            type="text"
            placeholder="Enter your username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <FormInput
            label="Password"
            type="password"
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <FormButton type="submit">Login</FormButton>
        </form>
        <p className="page-switch">
          Don't have an account? <Link to="/signup">Sign Up</Link>
        </p>
      </div>
    </div>
  );
};

export default LoginPage;
