import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000/api/';

/**
 * Gets a cookie value by name
 * @param {string} name - Cookie name to retrieve
 * @returns {string|null} Cookie value or null if not found
 */
function getCookie(name) {
  let cookieValue = null;
  if (document.cookie && document.cookie !== '') {
    const cookies = document.cookie.split(';');
    for (let cookie of cookies) {
      cookie = cookie.trim();
      if (cookie.startsWith(name + '=')) {
        cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
        break;
      }
    }
  }
  return cookieValue;
}

// Create axios instance
const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
  timeout: 30000, // 30 second timeout
  withCredentials: true, // Required for cookies to be sent
});

// Request interceptor
api.interceptors.request.use(
  (config) => {
    // Add auth token to requests
    const token = localStorage.getItem('token');
    if (token) {
      config.headers['Authorization'] = `Bearer ${token}`;
    }

    // Add CSRF token to unsafe methods
    if (['post', 'put', 'patch', 'delete'].includes(config.method)) {
      const csrfToken = getCookie('csrftoken');
      if (!csrfToken) {
        console.warn('CSRF token not found for non-GET request. This may cause a 403 error.');
      } else {
        config.headers['X-CSRFToken'] = csrfToken;
      }
    }

    return config;
  },
  (error) => Promise.reject(error)
);

// Response interceptor
api.interceptors.response.use(
  (response) => response,
  async (error) => {
    const customError = {
      message: error.response?.data?.detail || 'An unexpected error occurred',
      status: error.response?.status || 500,
      data: error.response?.data || {},
      originalError: error,
    };

    const originalRequest = error.config;

    // Handle network errors
    if (!error.response) {
      customError.message = 'Network error - please check your connection';
      return Promise.reject(customError);
    }

    // Handle CSRF token errors
    if (error.response.status === 403 &&
        error.response.data?.detail?.includes('CSRF')) {
      console.error('CSRF token validation failed. Attempting to refresh token...');
      try {
        await axios.get(`${API_URL.replace('/api/', '')}/csrf/`, { withCredentials: true });
        // Retry the original request with new CSRF token
        const csrfToken = getCookie('csrftoken');
        originalRequest.headers['X-CSRFToken'] = csrfToken;
        return api(originalRequest);
      } catch (refreshError) {
        console.error('Failed to refresh CSRF token:', refreshError);
        customError.message = 'Failed to validate your session. Please reload the page.';
        return Promise.reject(customError);
      }
    }

    // Handle token expiration
    if (error.response.status === 401 && !originalRequest._retry) {
      originalRequest._retry = true;

      try {
        const refreshToken = localStorage.getItem('refreshToken');
        if (!refreshToken) {
          throw new Error('No refresh token available');
        }

        // Attempt to refresh token
        const response = await axios.post(`${API_URL}token/refresh/`, {
          refresh: refreshToken,
        }, {
          withCredentials: true,
          headers: {
            'Content-Type': 'application/json',
            'X-CSRFToken': getCookie('csrftoken')
          }
        });

        // If token refresh successful, retry original request
        const { access } = response.data;
        localStorage.setItem('token', access);

        originalRequest.headers['Authorization'] = `Bearer ${access}`;
        return api(originalRequest);
      } catch (refreshError) {
        // If token refresh fails, redirect to login
        localStorage.removeItem('token');
        localStorage.removeItem('refreshToken');
        localStorage.removeItem('user');

        customError.message = 'Your session has expired. Please log in again.';
        customError.isAuthError = true;

        if (typeof window !== 'undefined') {
          window.location.href = '/login';
        }

        return Promise.reject(customError);
      }
    }

    // Handle common error statuses
    switch (error.response.status) {
      case 400:
        customError.message = 'Invalid request. Please check your data.';
        break;
      case 403:
        customError.message = 'You do not have permission to perform this action.';
        break;
      case 404:
        customError.message = 'The requested resource was not found.';
        break;
      case 429:
        customError.message = 'Too many requests. Please try again later.';
        break;
      case 500:
        customError.message = 'Server error. Please try again later.';
        break;
      default:
        break;
    }

    // Process validation errors
    if (error.response.status === 400 && typeof error.response.data === 'object') {
      try {
        const messages = [];
        Object.entries(error.response.data).forEach(([field, errors]) => {
          if (Array.isArray(errors)) {
            messages.push(`${field}: ${errors.join(', ')}`);
          } else if (typeof errors === 'string') {
            messages.push(`${field}: ${errors}`);
          }
        });

        if (messages.length > 0) {
          customError.message = messages.join('\n');
          customError.validationErrors = error.response.data;
        }
      } catch (e) {
        console.error('Error processing validation errors', e);
      }
    }

    return Promise.reject(customError);
  }
);

// Export error handler
export const handleApiError = (error, notifyFn = null) => {
  const message = error.message || 'An unexpected error occurred';
  const status = error.status || 500;

  console.error(`API Error (${status}):`, message, error);

  if (notifyFn && typeof notifyFn === 'function') {
    notifyFn({
      type: 'error',
      message: message,
      title: `Error ${status}`,
      duration: 5000,
    });
  }

  return {
    message,
    status,
    data: error.data || {},
    validationErrors: error.validationErrors || {},
    isAuthError: error.isAuthError || false,
  };
};

export default api;
