import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { FaMoon, FaSun, FaGlobe, FaUser } from "react-icons/fa";
import "./Navbar.css";

const Navbar = () => {
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // Check localStorage for a token (or user data)
    const token = localStorage.getItem("token");
    setIsLoggedIn(!!token);
  }, []);

  const handleThemeToggle = () => {
    setIsDarkMode(!isDarkMode);
    document.body.classList.toggle("dark-mode", !isDarkMode);
  };

  const handleLanguageChange = () => {
    alert("Language change not implemented yet!");
  };

  const handleProfileClick = () => {
    if (!isLoggedIn) {
      // Navigate to login/signup if not logged in
      navigate("/login");
    } else {
      // Toggle the profile dropdown menu
      setShowProfileDropdown(!showProfileDropdown);
    }
  };

  const handleLogout = () => {
    // Remove token and update state
    localStorage.removeItem("token");
    setIsLoggedIn(false);
    setShowProfileDropdown(false);
    navigate("/");
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <Link to="/" className="logo-link">
          <img src="/logo.png" alt="Militex Logo" className="logo-image" />
          <span className="logo-text">Militex</span>
        </Link>
      </div>

      <ul className="navbar-links">
        <li><Link to="/">Home</Link></li>
        <li><Link to="/buy">Buy</Link></li>
        <li><Link to="/sell">Sell</Link></li>
        <li><Link to="/tuning">Tuning</Link></li>
      </ul>

      <div className="navbar-right">
        <button onClick={handleThemeToggle} className="icon-button">
          {isDarkMode ? <FaSun /> : <FaMoon />}
        </button>
        <button onClick={handleLanguageChange} className="icon-button">
          <FaGlobe />
        </button>
        <div className="profile-wrapper">
          <button onClick={handleProfileClick} className="icon-button">
            <FaUser />
          </button>
          {showProfileDropdown && isLoggedIn && (
            <div className="profile-dropdown">
              <Link to="/profile" onClick={() => setShowProfileDropdown(false)}>
                Profile
              </Link>
              <button onClick={handleLogout}>Log Out</button>
            </div>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
