import React, { useState } from 'react';
import AuthService from '../../services/auth.service';

const AuthTest = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [currentUser, setCurrentUser] = useState(
    localStorage.getItem('user') ? JSON.parse(localStorage.getItem('user')) : null
  );

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setResult(null);

    try {
      const response = await AuthService.login(username, password);
      setResult('Login successful! Token received.');

      // Get current user
      const userData = await AuthService.getCurrentUser();
      setCurrentUser(userData);
    } catch (err) {
      console.error('Login test error:', err);
      setError(err.message || 'Login failed');
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    AuthService.logout();
    setCurrentUser(null);
    setResult('Logged out successfully');
  };

  return (
    <div className="p-6 bg-white rounded-lg shadow-md mt-6">
      <h2 className="text-xl font-bold text-indigo-900 mb-4">Authentication Test</h2>

      {currentUser ? (
        <div className="mb-4">
          <div className="p-4 bg-green-100 text-green-700 rounded-lg">
            <p><strong>Currently logged in as:</strong> {currentUser.username}</p>
            <p><strong>Email:</strong> {currentUser.email}</p>
            <p><strong>Military status:</strong> {currentUser.is_military ? 'Military' : 'Civilian'}</p>
          </div>
          <button
            onClick={handleLogout}
            className="mt-4 px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700"
          >
            Logout
          </button>
        </div>
      ) : (
        <form onSubmit={handleLogin} className="space-y-4">
          <div>
            <label className="block text-gray-700 mb-2" htmlFor="username">
              Username
            </label>
            <input
              id="username"
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
              required
            />
          </div>

          <div>
            <label className="block text-gray-700 mb-2" htmlFor="password">
              Password
            </label>
            <input
              id="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
              required
            />
          </div>

          <button
            type="submit"
            className="px-4 py-2 bg-indigo-900 text-white rounded hover:bg-indigo-800"
            disabled={loading}
          >
            {loading ? 'Testing...' : 'Test Login'}
          </button>
        </form>
      )}

      {result && (
        <div className="mt-4 p-4 bg-green-100 text-green-700 rounded-lg">
          {result}
        </div>
      )}

      {error && (
        <div className="mt-4 p-4 bg-red-100 text-red-700 rounded-lg">
          <p><strong>Error:</strong> {error}</p>
        </div>
      )}
    </div>
  );
};

export default AuthTest;
