import { useState } from 'react';
import { User, LogIn, LogOut, Menu } from 'lucide-react';

export default function Navbar() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const handleLogin = () => {
    setIsLoggedIn(true);
    setIsMenuOpen(false);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setIsMenuOpen(false);
  };

  return (
    <nav className="bg-purple-900 text-white p-4 flex items-center justify-between">
      {/* Logo */}
      <div className="flex items-center">
        <img src="/api/placeholder/40/40" alt="Logo" className="h-10 w-10 rounded-full" />
      </div>

      {/* Navigation Links */}
      <div className="hidden md:flex space-x-8">
        <a href="#" className="hover:text-purple-300">Home</a>
        <a href="#" className="hover:text-purple-300">Buy</a>
        <a href="#" className="hover:text-purple-300">Sell</a>
        <a href="#" className="hover:text-purple-300">Fundraiser</a>
      </div>

      {/* Dark Mode and Language (Placeholders) */}
      <div className="flex items-center space-x-4">
        <button className="p-2 rounded-full hover:bg-purple-800">
          <span className="text-lg">üåô</span>
        </button>

        <button className="p-2 rounded-full hover:bg-purple-800">
          <span className="text-lg">üåê</span>
        </button>

        {/* Account Dropdown */}
        <div className="relative">
          <button
            onClick={toggleMenu}
            className="p-2 rounded-full hover:bg-purple-800"
          >
            <User size={24} />
          </button>

          {/* Dropdown Menu */}
          {isMenuOpen && (
            <div className="absolute right-0 mt-2 w-48 bg-white text-gray-800 rounded-md shadow-lg py-1 z-10">
              {isLoggedIn ? (
                <>
                  <a href="#" className="flex items-center px-4 py-2 hover:bg-gray-100">
                    <User size={16} className="mr-2" /> Profile
                  </a>
                  <button
                    onClick={handleLogout}
                    className="flex items-center w-full text-left px-4 py-2 hover:bg-gray-100"
                  >
                    <LogOut size={16} className="mr-2" /> Sign Out
                  </button>
                </>
              ) : (
                <>
                  <button
                    onClick={handleLogin}
                    className="flex items-center w-full text-left px-4 py-2 hover:bg-gray-100"
                  >
                    <LogIn size={16} className="mr-2" /> Login
                  </button>
                  <a href="#" className="flex items-center px-4 py-2 hover:bg-gray-100">
                    <User size={16} className="mr-2" /> Sign Up
                  </a>
                </>
              )}
            </div>
          )}
        </div>

        {/* Mobile Menu Button (visible on small screens) */}
        <button className="md:hidden p-2 rounded-full hover:bg-purple-800">
          <Menu size={24} />
        </button>
      </div>
    </nav>
  );
}
