import React, { useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import { Link } from 'react-router-dom';
import CarFilters from './CarFilters';
import CarCard from './CarCard';

const CarList = () => {
  const { t } = useTranslation();
  const [cars, setCars] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [filters, setFilters] = useState({});
  const [pagination, setPagination] = useState({
    count: 0,
    next: null,
    previous: null,
  });
  const [currentPage, setCurrentPage] = useState(1);

  useEffect(() => {
    fetchCars();
  }, [filters, currentPage]);

  const fetchCars = async () => {
    setLoading(true);
    try {
      // Combine filters with pagination
      const params = new URLSearchParams({
        ...filters,
        page: currentPage,
      }).toString();

      console.log('Fetching cars with params:', params);

      // Try making a direct fetch request instead of using the service
      const url = params ? `/api/cars/?${params}` : '/api/cars/';
      const response = await fetch(url);

      if (!response.ok) {
        throw new Error(`API responded with status ${response.status}`);
      }

      const data = await response.json();
      console.log('Cars API response:', data);

      if (data.results) {
        setCars(data.results);
        setPagination({
          count: data.count || 0,
          next: data.next,
          previous: data.previous,
        });
      } else {
        setError('Invalid response format from API');
      }
    } catch (error) {
      console.error('Failed to fetch cars:', error);
      setError(`Failed to fetch cars: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const handleFilterChange = (newFilters) => {
    setFilters(newFilters);
    setCurrentPage(1); // Reset to first page when filters change
  };

  const handlePreviousPage = () => {
    if (pagination.previous) {
      setCurrentPage(currentPage - 1);
    }
  };

  const handleNextPage = () => {
    if (pagination.next) {
      setCurrentPage(currentPage + 1);
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      {/* Add debug information */}
      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          <p className="font-bold">Error!</p>
          <p>{error}</p>
        </div>
      )}

      <div className="flex flex-col md:flex-row gap-6">
        {/* Filters */}
        <div className="w-full md:w-1/4">
          <CarFilters onFilterChange={handleFilterChange} />
        </div>

        {/* Car Listings */}
        <div className="w-full md:w-3/4">
          {/* Search Bar */}
          <div className="mb-6 flex">
            <input
              type="text"
              placeholder={t('common.search')}
              className="w-full px-4 py-2 border rounded-l-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
              onChange={(e) =>
                handleFilterChange({ ...filters, search: e.target.value })
              }
            />
            <button className="bg-indigo-900 text-white px-4 py-2 rounded-r-lg hover:bg-indigo-800">
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"></path>
              </svg>
            </button>
          </div>

          {/* Cars Grid */}
          {loading ? (
            <div className="flex justify-center py-8">
              <div className="loader"></div>
            </div>
          ) : cars.length === 0 ? (
            <div className="text-center py-8">
              <p className="text-gray-600">No cars found matching your criteria.</p>
            </div>
          ) : (
            <div className="space-y-4">
              {cars.map((car) => (
                <CarCard key={car.id} car={car} />
              ))}
            </div>
          )}

          {/* Pagination */}
          {pagination.count > 0 && (
            <div className="flex justify-between items-center mt-8">
              <button
                onClick={handlePreviousPage}
                disabled={!pagination.previous}
                className={`px-4 py-2 rounded ${
                  pagination.previous ? 'bg-indigo-900 text-white hover:bg-indigo-800' : 'bg-gray-300 text-gray-500 cursor-not-allowed'
                }`}
              >
                {t('common.previous')}
              </button>

              <span className="text-gray-600">
                Page {currentPage} of {Math.ceil(pagination.count / 10)}
              </span>

              <button
                onClick={handleNextPage}
                disabled={!pagination.next}
                className={`px-4 py-2 rounded ${
                  pagination.next ? 'bg-indigo-900 text-white hover:bg-indigo-800' : 'bg-gray-300 text-gray-500 cursor-not-allowed'
                }`}
              >
                {t('common.next')}
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default CarList;
