import React, { useState } from "react";
import "./SellCarPage.css";

function SellCarPage() {
  const [step, setStep] = useState(1);

  // Form data stored in a single state or multiple states
  const [formData, setFormData] = useState({
    carMake: "",
    carModel: "",
    year: "",
    mileage: "",
    vehicleType: "",
    condition: "",
    fuelType: "",
    transmission: "",
    country: "",
    city: "",
    price: "",
    negotiable: false,
    description: "",
    photo: null,
  });

  // Helper function to update form data
  const handleChange = (field, value) => {
    setFormData((prev) => ({ ...prev, [field]: value }));
  };

  const nextStep = () => setStep((prev) => prev + 1);
  const prevStep = () => setStep((prev) => prev - 1);

  const handleSell = () => {
    // TODO: Implement final form submission logic
    alert("Car sold! (not really, implement logic here)");
  };

  return (
    <div className="sell-container">
      {/* Multi-step form container */}
      <div className="sell-form">
        {step === 1 && (
          <StepOne
            formData={formData}
            onChange={handleChange}
            onNext={nextStep}
          />
        )}
        {step === 2 && (
          <StepTwo
            formData={formData}
            onChange={handleChange}
            onNext={nextStep}
            onPrev={prevStep}
          />
        )}
        {step === 3 && (
          <StepThree
            formData={formData}
            onPrev={prevStep}
            onSell={handleSell}
          />
        )}
      </div>
    </div>
  );
}

export default SellCarPage;

/* --------------------
   Step One Component
-------------------- */
function StepOne({ formData, onChange, onNext }) {
  return (
    <div className="step-card">
      <h1 className="step-title">Sell your car</h1>

      <div className="form-group">
        <label>Car Make</label>
        <input
          type="text"
          value={formData.carMake}
          onChange={(e) => onChange("carMake", e.target.value)}
          placeholder="Car Make"
        />
      </div>

      <div className="form-group">
        <label>Car Model</label>
        <input
          type="text"
          value={formData.carModel}
          onChange={(e) => onChange("carModel", e.target.value)}
          placeholder="Car Model"
        />
      </div>

      <div className="two-columns">
        <div className="form-group">
          <label>Year of Manufacture</label>
          <input
            type="text"
            value={formData.year}
            onChange={(e) => onChange("year", e.target.value)}
            placeholder="e.g. 2015"
          />
        </div>
        <div className="form-group">
          <label>Mileage</label>
          <input
            type="text"
            value={formData.mileage}
            onChange={(e) => onChange("mileage", e.target.value)}
            placeholder="e.g. 123 456"
          />
        </div>
      </div>

      <div className="two-columns">
        <div className="form-group">
          <label>Vehicle Type</label>
          <input
            type="text"
            value={formData.vehicleType}
            onChange={(e) => onChange("vehicleType", e.target.value)}
            placeholder="e.g. Pickup"
          />
        </div>
        <div className="form-group">
          <label>Condition</label>
          <input
            type="text"
            value={formData.condition}
            onChange={(e) => onChange("condition", e.target.value)}
            placeholder="Used / New"
          />
        </div>
      </div>

      <div className="two-columns">
        <div className="form-group">
          <label>Fuel Type</label>
          <input
            type="text"
            value={formData.fuelType}
            onChange={(e) => onChange("fuelType", e.target.value)}
            placeholder="e.g. Diesel"
          />
        </div>
        <div className="form-group">
          <label>Transmission</label>
          <input
            type="text"
            value={formData.transmission}
            onChange={(e) => onChange("transmission", e.target.value)}
            placeholder="e.g. Manual"
          />
        </div>
      </div>

      <button className="form-button next-button" onClick={onNext}>
        Next →
      </button>
    </div>
  );
}

/* --------------------
   Step Two Component
-------------------- */
function StepTwo({ formData, onChange, onNext, onPrev }) {
  return (
    <div className="step-card">
      <h1 className="step-title">Sell your car</h1>

      <div className="form-group">
        <label>Country</label>
        <input
          type="text"
          value={formData.country}
          onChange={(e) => onChange("country", e.target.value)}
          placeholder="Country"
        />
      </div>

      <div className="form-group">
        <label>City/Region</label>
        <input
          type="text"
          value={formData.city}
          onChange={(e) => onChange("city", e.target.value)}
          placeholder="City"
        />
      </div>

      <div className="form-group">
        <label>Price</label>
        <input
          type="text"
          value={formData.price}
          onChange={(e) => onChange("price", e.target.value)}
          placeholder="Car Price"
        />
      </div>

      <div className="form-group negotiable-row">
        <label>Negotiable Price?</label>
        <input
          type="checkbox"
          checked={formData.negotiable}
          onChange={(e) => onChange("negotiable", e.target.checked)}
        />
      </div>

      <div className="form-group">
        <label>Car Description</label>
        <textarea
          value={formData.description}
          onChange={(e) => onChange("description", e.target.value)}
          placeholder="Describe your car..."
        />
      </div>

      <div className="form-group">
        <label>Upload Photo</label>
        <input
          type="file"
          onChange={(e) => onChange("photo", e.target.files[0])}
        />
      </div>

      <div className="form-nav">
        <button className="form-button back-button" onClick={onPrev}>
          ← Back
        </button>
        <button className="form-button next-button" onClick={onNext}>
          Next →
        </button>
      </div>
    </div>
  );
}

/* --------------------
   Step Three Component
-------------------- */
function StepThree({ formData, onPrev, onSell }) {
  return (
    <div className="step-card">
      <h1 className="step-title">Sell your car</h1>

      <p className="final-check">All right?</p>

      {/* You can display a summary of the user’s input if you want */}
      <div className="summary">
        <p>
          <strong>Make:</strong> {formData.carMake || "N/A"} <br />
          <strong>Model:</strong> {formData.carModel || "N/A"} <br />
          <strong>Year:</strong> {formData.year || "N/A"} <br />
          <strong>Mileage:</strong> {formData.mileage || "N/A"} <br />
          <strong>Vehicle Type:</strong> {formData.vehicleType || "N/A"} <br />
          <strong>Condition:</strong> {formData.condition || "N/A"} <br />
          <strong>Fuel:</strong> {formData.fuelType || "N/A"} <br />
          <strong>Transmission:</strong> {formData.transmission || "N/A"} <br />
          <strong>Country:</strong> {formData.country || "N/A"} <br />
          <strong>City:</strong> {formData.city || "N/A"} <br />
          <strong>Price:</strong> {formData.price || "N/A"}{" "}
          {formData.negotiable ? "(negotiable)" : ""}
          <br />
          <strong>Description:</strong> {formData.description || "N/A"} <br />
        </p>
      </div>

      <div className="form-nav">
        <button className="form-button back-button" onClick={onPrev}>
          ← Back
        </button>
        <button className="form-button next-button" onClick={onSell} >
          Sell Car
        </button>
      </div>
    </div>
  );
}
